// 버튼 누르면 그 데이터에 생성해서 그거 있자녀 암튼 그거임



function InputToDo(props) {
    let navigate = useNavigate()

    return (
      <div>
        <div style={{padding: '10px'}}>
            <input onChange={(e) => 
            changeDate(e, props.setDate)} 
            type="text" 
            name="date" 
            placeholder="언제까지 완료할 건가요?" 
            className='inputDate'></input>
        </div>

        <div style={{padding: '10px'}}>
            <input onChange={(e) => 
            changeTitle(e, props.setTitle)} 
            type="text" 
            name="title" 
            placeholder="할 일을 입력하세요" 
            className='inputContent'></input>
        </div>

        <button type="submit" className="submitButton" onClick={() => {
            addTodo(props.title, props.date, props.setTodo, props.setTitle, props.setDate, navigate)

        }}>todo!</button>

        <ul>
            {props.todo.map((a, i) => (
            <div>
                <h4>{a.title}</h4> <p>{a.date}</p>
            </div>
            ))}
        </ul>
      </div>
    );
  }

export default List
