import axios from "axios"

const changeTitle = (e, setTitle) => {
    setTitle(e.target.value) 
}
const changeDate = (e, setDate) => {
    setDate(e.target.value)
}
const addTodo = (title, date, setTodo, setTitle, setDate, navigate) => {
    const newTodo = {
        title: title,
        date: date
    };
    setTodo(prevTodo => [...prevTodo, newTodo]);
    setTitle('');
    setDate('');

    axios.post('http://localhost:8000/add', newTodo)
        .then((res) => {
            navigate('/list')
            console.log(res.data)
        })
        .catch((err) => {
            console.log(err)
    })

    let input = document.querySelector('.inputDate')
    input.value = ''
    input = document.querySelector('.inputTitle')
    input.value = ''
}

const fetchTodo = () => {
    axios.get('http://localhost:8000/add')  
        .then((res) => { 
            setTodo(res.data)
            console.log(res.data)
        })
        .catch((err) => {
            console.log(err)
    })
}

const deleteTodo = (id, i) => {
    if(window.confirm('정말 삭제할까요?')) {
        let copy = [...todo]
        copy.splice(i,1)
        setTodo(copy)
        alert('삭제되었습니다.')

        axios.delete(`http://localhost:8000/delete/${id}`)
        .then((res) => { 
            console.log(res.data)
        })
        .catch((err) => {
            console.log(err)
        })
    }
}


export {changeDate, changeTitle, addTodo, fetchTodo, deleteTodo}
