import { Button, Container, Nav, Navbar } from 'react-bootstrap';
import { Link, Route, Routes, useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react';
import axios from "axios"

import {changeDate, changeTitle, addTodo} from './Function'


function List() {
    let [date, setDate] = useState("")
    let [title, setTitle] = useState("")
    let [todo, setTodo] = useState([])
    let [completed, setCompleted] = useState(true)
    let [checked, setChecked] = useState([])
    let [writer, setWriter] = useState('');

    let navigate = useNavigate()

    useEffect(() => {
        fetchTodo()
    }, [])


    const fetchTodo = () => {
        axios.get('http://localhost:8000/add')  
            .then((res) => { 
                setTodo(res.data[0])
                setWriter(res.data[1]) //req.user.id Í∞ÄÏ†∏Ïò¥
                console.log(res.data)
            })
            .catch((err) => {
                console.log(err) 
        })
    }

    const deleteTodo = (id, i) => {
        if(window.confirm('Ï†ïÎßê ÏÇ≠Ï†úÌï†ÍπåÏöî?')) {
            axios.delete(`http://localhost:8000/delete/${id}`)
            .then((res) => { 
                let copy = [...todo]
                copy.splice(i,1)
                setTodo(copy)
                alert('ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.')
                console.log('res.data : ' + res.data)
            })
            .catch((err) => {
                console.log(err)
            })
        }
    }

    const getChecked = (checked, id) => {
        if (checked) {
            setChecked(prev => [...prev, id])
        }
        else {
            setChecked(checked.filter((v) => v != id))
        }
    }

    return (
        <div> 
            <div className='title'>
                <h1 className='homeFont'> To Do List </h1>
            </div>
            {/* <p className='listTitle'> My List </p> */}
            <div className='container'>
                <div className='inputBox'>
                    <InputToDo style={{justifyContent: "space-between"}} title={title} setTitle={setTitle} date={date} setDate={setDate} todo={todo} setTodo={setTodo} completed={completed} setCompleted={setCompleted}></InputToDo> 
                </div>  

                <div className='listBox'>
                    {
                        todo.map((a, i) => {
                            if (writer == a.writer) {
                                return (
                                    <div className='listItem' key={i}>
                                        <input 
                                        type='checkbox' 
                                        onChange={(e) => getChecked(e.target.checked, a._id)}
                                        checked={checked.includes(a._id) ? true : false}/>
                                        <div>
                                            <span style={{whiteSpace: "nowrap"}}>{a.title}</span>
                                            <span>{a.date}</span>
                                        </div>
                                        <button className='deleteButton' data-id={todo._id} onClick={() => {
                                            var id = a._id
                                            deleteTodo(id, i)
                                        }} > üóëÔ∏è </button>
                                    </div> 
                                )
                            }
                        })
                    }
                    <div className='listItem'></div>
                </div>
            </div>
        </div>
    )
}

function InputToDo(props) {

    let navigate = useNavigate()

    return (
      <div>
        <div style={{padding: '10px'}}>
            <input onChange={(e) => 
            changeDate(e, props.setDate)} 
            type="date" 
            className='inputDate'></input>
        </div>

        <div style={{padding: '10px'}}>
            <input onChange={(e) => 
            changeTitle(e, props.setTitle)} 
            type="text" 
            placeholder="Ìï† ÏùºÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî" 
            className='inputTitle'></input>
        </div>

        <button type="submit" className="submitButton" onClick={() => {
            addTodo(props.title, props.date, props.completed, props.setTodo, props.setTitle, props.setDate, props.setCompleted, navigate)
        }}>todo!</button>
      </div>
    );
  }

export default List
